import React,{useState,useEffect} from 'react';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Link } from "react-router-dom";
import { Required } from '../../utilities/validator';

function Add(props) {

    const [Form_data, setForm_data] = useState({
        name:{
            value:"",
            isValid:false,
            error:"",
            rules:["required"]
        },
        author:{
            value:"",
            isValid:false,
            error:"",
            rules:["required"]
        },
        price:{
            value:"",
            isValid:false,
            error:"",
            rules:["required"]
        }
    })

    const setFormData=(name,value)=>{
        Form_data[`${name}`]=value;
        console.log(Form_data);
    }

    const validate = ()=>{
        for (const key in Form_data) {
            if (Object.hasOwnProperty.call(Form_data, key)) {
                const element = Form_data[key];
                console.log(element);
                if (element.rules) {
                    let result=Required(element.value);
                }
            }
        }
    }

    const onFormSubmit=()=>{
        validate();
    }

    return (
        <>
            <div className="container mt-5">
        
                <Card style={{ width: 'auto' }}>
                    <Card.Header>
                        <div className="row">
                            <div className="col-md-6">
                                <Card.Title className='mt-2'>Create New Book</Card.Title>
                            </div>
                            <div className="col-md-6">
                                <div className="d-flex justify-content-end">
                                    <Link to={'/books'}>
                                        <button className='btn btn-primary'>Back</button>
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </Card.Header>
                    <Card.Body>
                        <Form>   
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label className='font-weight-bold'>Name</Form.Label>
                                <Form.Control type="text" name='name' onInput={(e)=>{setFormData(e.target.name,e.target.value)}} placeholder="Enter Book Name.." required />
                                    {/* <Form.Text className="text-danger">
                                    We'll never share your email with anyone else.
                                    </Form.Text> */}
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label className='font-weight-bold'>Author</Form.Label>
                                <Form.Control type="text" name='author' onInput={(e)=>{setFormData(e.target.name,e.target.value)}} placeholder="Enter Author Name.." required />
                                    {/* <Form.Text className="text-danger">
                                    We'll never share your email with anyone else.
                                </Form.Text> */}
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label className='font-weight-bold'>Price</Form.Label>
                                <Form.Control type="number" name='price' onInput={(e)=>{setFormData(e.target.name,e.target.value)}} step={0.01} placeholder="Enter Price.." required />
                                    {/* <Form.Text className="text-danger">
                                    We'll never share your email with anyone else.
                                </Form.Text> */}
                            </Form.Group>
                            <Button variant="primary" onClick={()=>{onFormSubmit()}}>
                                Submit
                            </Button>
                        </Form>
                    </Card.Body>
                </Card>
            </div>                    
        </>
    );
}

export default Add;
