import React,{useState,useEffect} from 'react'
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';
import {GET} from '../../utilities/apiHelper';
export default function Index() {

  const [Bookslist, setBookslist] = useState({});

  const getBooks=async ()=>{
    try {
        let result=await GET("/books");
        setBookslist(result.serverData);
        console.log(Bookslist);
        
    } catch (error) {
        console.log("Javascript Error Occured"+error);
    }
  }

  useEffect(() => {
      getBooks();
      return () => {
    }
  }, [])
  

  return (
   <>
    <div className="container mt-5">
        
    <Card style={{ width: 'auto' }}>
    <Card.Header>
        <div className="row">
            <div className="col-md-6">
                <Card.Title>Books List</Card.Title>
            </div>
            <div className="col-md-6">
                <div className="d-flex justify-content-end">
                    <button className='btn btn-primary'>Add</button>
                </div>
            </div>
        </div>
    </Card.Header>
      <Card.Body>
      <Table striped bordered hover>
      <thead>
        <tr>
          <th>Sr no</th>
          <th>Name</th>
          <th>Author</th>
          <th>Price</th>
        </tr>
      </thead>
      <tbody>
        {Bookslist.count > 0 ?'':
        
        <tr>
          <td colSpan={4} className="text-center">No Books Available</td>
        </tr>
        }
        
      </tbody>
    </Table>
      </Card.Body>
    </Card>

    
    </div>
   </>
  )
}
