import React,{useState,useEffect} from 'react'
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';
import {GET} from '../../utilities/apiHelper';
import { Link } from "react-router-dom";
export default function Index() {

  const [Bookslist, setBookslist] = useState({});

  const getBooks=async ()=>{
    try {
        let result=await GET("/books");
        setBookslist(result.data.serverData);
        console.log(Bookslist);
        
    } catch (error) {
        console.log("Javascript Error Occured"+error);
    }
  }

  useEffect(() => {
      getBooks();
      return () => {
    }
  }, [])
  

  return (
   <>
    <div className="container mt-5">
        
    <Card style={{ width: 'auto' }}>
    <Card.Header>
        <div className="row">
            <div className="col-md-6">
                <Card.Title className='mt-2'>Books List</Card.Title>
            </div>
            <div className="col-md-6">
                <div className="d-flex justify-content-end">
                    <Link to={'/books/new'}>
                        <button className='btn btn-primary'>Add</button>
                    </Link>
                </div>
            </div>
        </div>
    </Card.Header>
      <Card.Body>
      <Table striped bordered hover>
      <thead>
        <tr>
          <th>Sr no</th>
          <th>Name</th>
          <th>Author</th>
          <th>Price</th>
          <th>Availablity</th>
        </tr>
      </thead>
      <tbody>
        {Bookslist.count > 0 ?
            Bookslist.data.map((record,index)=>(
                <tr key={record.id}>
                    <td>{index+1}</td>
                    <td>{record.name}</td>
                    <td>{record.author}</td>
                    <td>{record.price}</td>
                    <td>{record.availablity==0?"Available":"Asssigned"}</td>
                </tr>
            ))
        :
        
        <tr>
          <td colSpan={4} className="text-center">No Books Available</td>
        </tr>
        }
        
      </tbody>
    </Table>
      </Card.Body>
    </Card>

    
    </div>
   </>
  )
}
