import React,{useState,useEffect} from 'react'
import Table from 'react-bootstrap/Table';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import {GET,DELETE} from '../../utilities/apiHelper';
import { Link } from "react-router-dom";
export default function Index() {


  const [Bookslist, setBookslist] = useState({});

  const getBooks=async ()=>{
    try {
        let result=await GET("/books");
        setBookslist(result.data.serverData);
        console.log(Bookslist);
        
    } catch (error) {
        console.log("Javascript Error Occured"+error);
    }
  }

  const DeleteBook =async(id) =>{
    try {
        let result=await DELETE(`/books/${id}`);
        if (result.status==200) {
            getBooks();
        }
    } catch (error) {
        console.log("Javascript Error Occured"+error);
    }
  }

  useEffect(() => {
      getBooks();
      return () => {
    }
  }, [])
  

  return (
   <>
    <div className="container mt-5">
        
    <Card style={{ width: 'auto' }}>
    <Card.Header>
        <div className="row">
            <div className="col-md-6">
                <Card.Title className='mt-2'>Books List</Card.Title>
            </div>
            <div className="col-md-6">
                <div className="d-flex justify-content-end">
                    <Link to={'/books/new'}>
                        <button className='btn btn-primary'>Add</button>
                    </Link>
                </div>
            </div>
        </div>
    </Card.Header>
      <Card.Body>
      <Table striped bordered hover>
      <thead>
        <tr>
          <th>Sr no</th>
          <th>Name</th>
          <th>Author</th>
          <th>Price</th>
          <th>Availablity</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {Bookslist.count > 0 ?
            Bookslist.data.map((record,index)=>(
                <tr key={record.id}>
                    <td>{index+1}</td>
                    <td>{record.name}</td>
                    <td>{record.author}</td>
                    <td>{record.price}</td>
                    <td>{record.availablity==0?"Available":"Asssigned"}</td>
                    <td>
                        <div className="d-flex">
                            <Link to={`/books/${record.id}`}>
                                <button className='btn btn-info btn-sm'>edit</button>
                            </Link>
                            &nbsp;&nbsp;
                            <button onClick={()=>{DeleteBook(record.id)}} className='btn btn-danger btn-sm'>delete</button>
                            &nbsp;&nbsp;
                            <button onClick={()=>{}} className='btn btn-warning btn-sm'>assign</button>
                        </div>
                    </td>
                </tr>
            ))
        :
        
        <tr>
          <td colSpan={4} className="text-center">No Books Available</td>
        </tr>
        }
        
      </tbody>
    </Table>
      </Card.Body>
    </Card>
    <ModalComp/>
    
    </div>
   </>
  )
}




function ModalComp(isVisible) {
  const [show, setShow] = useState(isVisible);
  const handleClose = () => setShow(false);
//   const handleShow = () => setShow(true);

  return (
    <>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
